import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { fetchAnimals } from '../store/slices/animalsSlice';
import { animalService } from '../services/animalService';
import { toast } from 'react-toastify';
import './FarmerDashboard.css';

const FarmerDashboard = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { user, isAuthenticated } = useSelector((state) => state.auth);
  const { animals } = useSelector((state) => state.animals);
  const [showAnimalForm, setShowAnimalForm] = useState(false);
  const [animalForm, setAnimalForm] = useState({
    name: '',
    animal_type: 'cattle',
    breed: '',
    age: '',
    price: '',
    weight: '',
    description: '',
    image_url: ''
  });

  useEffect(() => {
    // Redirect if not authenticated
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }

    // Only load animals if user is authenticated and is a farmer
    if (isAuthenticated && user && user.user_type === 'farmer') {
      dispatch(fetchAnimals());
    }
  }, [dispatch, user, isAuthenticated, navigate]);

  const handleAnimalSubmit = async (e) => {
    e.preventDefault();
    try {
      await animalService.createAnimal(animalForm);
      toast.success('Animal added successfully!');
      setShowAnimalForm(false);
      setAnimalForm({
        name: '',
        animal_type: 'cattle',
        breed: '',
        age: '',
        price: '',
        weight: '',
        description: '',
        image_url: ''
      });
      dispatch(fetchAnimals());
    } catch (error) {
      toast.error('Failed to add animal');
    }
  };

  const handleDeleteAnimal = async (animalId) => {
    if (window.confirm('Are you sure you want to delete this animal?')) {
      try {
        await animalService.deleteAnimal(animalId);
        toast.success('Animal deleted successfully!');
        dispatch(fetchAnimals());
      } catch (error) {
        toast.error('Failed to delete animal');
      }
    }
  };

  // Show loading while checking authentication
  if (!isAuthenticated || !user) {
    return <div className="container">Loading...</div>;
  }

  if (user.user_type !== 'farmer') {
    return (
      <div className="container">
        <div className="access-denied">
          <h2>Access Denied</h2>
          <p>This page is only available for farmers.</p>
        </div>
      </div>
    );
  }

  const myAnimals = animals.filter(animal => animal.farmer.id === user.id);

  return (
    <div className="farmer-dashboard">
      <div className="container">
        {/* ... rest of the component remains the same ... */}
      </div>
    </div>
  );
};

export default FarmerDashboard;
